template.declaration:
id_t tmp_i;
bound_t tmp_seg;

void reset_tmps()
{
    const bound_t fresh = {-1,0,0};
    tmp_i = 0;
    tmp_seg = fresh;
}

bool has_successor(const seg_t seg)
{
    return exists(i:o_t)succs[tmp_i][seg][i] > -1;
}

bool has_running()
{
    return exists(j:m_t) is_observing_c(j,tmp_i);
}

bool last()
{
    return blocked[tmp_i][0] == -1 && ready[tmp_i][0] == -1 && !has_running() && !has_successor(tmp_seg.id);
}

void update_preds()
{
    const seg_t tmp[O] = succs[tmp_i][tmp_seg.id];

    for(i : o_t)
    {
        for(j : i_t)
        {
            if(tmp[i] > -1 && preds[tmp_i][tmp[i]][j] == tmp_seg.id)
            {
               preds[tmp_i][tmp[i]][j] = -1;
            }   
        }
    }
}

void finish_segment()
{
    update_blocked_list(tmp_i,tmp_seg.id);
    update_preds();
    reset_tmps();
    ++avail_processors;
}

bool is_free(const m_t clk)
{    
    return active_c[clk] == -1;   
}

t_val_t period() {
    return period_list[id];
}

t_val_t offset() {
    return offset_list[id];
}

t_val_t deadline() {
    return deadline_list[id];
}
